import React, {useState} from "react";
import emailjs from 'emailjs-com';

function ForgotPassword() {
    emailjs.init('GONcF-cj5ZEW9CF6a');

    const [email, setEmail] = useState("");

    const templateParams = {
        email: email,
        OTP: Math.floor(100000 + Math.random() * 900000),
    }

    const sendEmail = e => {
        e.preventDefault();

        if (email === "") {
            alert('Email not valid.');
            return;
        }

        emailjs.send('service_miwbqi2', 'template_5f1t95s', templateParams)
            .then((res) => {
                alert('OTP to retrieve the password sent to the email if it exists.');
                window.location = '/resetPassword';
            }, err => {
                console.log(err.text);
            })
    };

    return (
        <div id={"forgot-page"}>
            <div id={"forgot-backButton"}>
                <a href={"/login"}
                   className={"arrow left"}>
                </a>
            </div>

            <form onSubmit={sendEmail}>
                <h3> Forgot Password </h3>

                <div>
                    <label> Email </label>
                    <input type={"email"}
                           placeholder={"Email"}
                           id={"emailInput"}
                    />
                </div>

                <button onClick={() => setEmail(document.getElementById("emailInput").value)}
                        type={"submit"}>
                    Forgot
                </button>
            </form>
        </div>
    );
}

export default ForgotPassword;
